styles:
.spinner-wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100px;
}

.spinner {
  animation: spin 1s linear infinite;
  width: 40px;
  height: 40px;
}

.path {
  stroke: #3498db; /* ðŸ”µ customize to your theme color */
  stroke-linecap: round;
}

@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
